import { Component } from '@angular/core';

@Component({
  selector: 'app-grandchild',
  templateUrl: './grandchild.component.html',
  styles: [
  ]
})
/**
 * Clase que incrementa y decrementa y reinicia el contador
 */
export class GrandchildComponent {

  /**
   * Contador
   */
  counter: number = 0;

  /**
   * Reinicia el contador a 0
   */
  resetCounter(){
    this.counter = 0;
    this.changeCoaunter.emit(this.counter);
  }
}
<h4>Contador</h4>
<h5>{{counter}}</h5>

<button (click)="resetCounter()" class="waves-effect waves-light btn red"><i class="material-icons right">replay</i>Reset</button>
