import { Component, ComponentFactoryResolver } from '@angular/core';
import { Store } from '@ngrx/store';
import { Observable, Subject } from 'rxjs';
import { GetPokemonTypeBackground, PokemonTypes } from './core/models/enums/pokemon-types.enum';
import { Types } from './core/models/pokemon/pokemon.model';
import { AppState } from './store/app-state/app-state.model';
import { DecreaseCounterAction } from './store/counter/actions/decrease-counter.action';
import { IncreaseCounterAction } from './store/counter/actions/increase-counter.action';
import { CounterModuleState } from './store/counter/app-state/module-state.model';
import { GetPokemonAction } from './store/pokemon/actions/get-pokemon.action';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

/**
 * Clase que incrementa y decrementa el contador padre
 */
export class AppComponent {
  title = 'pokedex-app';

  /**
   * Variable contador
   */
  counter: number = 10;

  /**
   * Objeto para quitar la suscripción
   */
  public unsubscribe: Subject<void> = new Subject<void>();

  /**
   * Tipos de pokemones.
  */
  public pokemonType = Object.values(PokemonTypes).filter(
    (value) => typeof value === "string"
  );

  /**
   * Tipos de pokemón.
   */
  public types!: Types[];

  /**
   * Color del backgroud obtenido por los tipos de pokemón.
   */
  public background?: string;

  public pokemonTypeBackground: { id: number; text?: string; }[] = [];
  /**
   * Constructor del componente.
   * @param store Almacén de estados de la aplicación.
   */
  constructor(private store: Store<AppState>){
    this.store.select(x => x.counter)
    .subscribe( counter => this.counter = counter);

     this.store.select(x => x.pokemon.types)
     .subscribe( type => this.types = type);
  }

  ngOnInit(){
    // const backgroud = document.getElementById('pokedex')
    // if (backgroud != null){
    //   backgroud.classList.add('green');
    // }
  }
  /**
  * Método que se ejecuta al destruir el componente.
  */
  ngOnDestroy(): void {
    this.unsubscribe.next();
    this.unsubscribe.complete();
  }

  /**
   * Incrementa el contador
   */
  increaseCounter(){
    this.getPokemonTypeBackground(this.types.map(x => x.type.name)[0]);
    console.log(this.types.map(x => x.type.name)[0])

    console.log(this.pokemonTypeBackground);
    this.store.dispatch(new IncreaseCounterAction);
    this.store.dispatch(new GetPokemonAction(this.counter));
  }

  /**
   * Decrementa el contador
   */
  decreaseCounter(){
    this.getPokemonTypeBackground(this.types.map(x => x.type.name)[0]);
    this.store.dispatch(new DecreaseCounterAction);
    this.store.dispatch(new GetPokemonAction(this.counter));
  }

  /**
   * Obtiene el background de acuerdo al tipo de pokemón del enumerable.
   * @param pokemonType Clasificación de alerta
   */
  getPokemonTypeBackground(pokemonType: string) {
    //let pokemonTypeBackground: { id: number; text?: string; }[] = [];
    this.pokemonType.forEach((type) => {
      if (type == pokemonType) {
        if (this.pokemonTypeBackground != null){
          this.pokemonTypeBackground.push({
            id: +type,
            text: GetPokemonTypeBackground.get(pokemonType),
          });
        }


      }
    });
    return this.pokemonTypeBackground;
  }
}

