import { Component } from '@angular/core';
import { Store } from '@ngrx/store';
import { Subject } from 'rxjs';
import { DecreaseCounterAction } from './store/counter/actions/decrease-counter.action';
import { IncreaseCounterAction } from './store/counter/actions/increase-counter.action';
import { CounterModuleState } from './store/counter/app-state/module-state.model';
import { GetPokemonAction } from './store/pokemon/actions/get-pokemon.action';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

/**
 * Clase que incrementa y decrementa el contador padre
 */
export class AppComponent {
  title = 'pokedex-app';

  /**
   * Variable contador
   */
  counter: number = 10;

  /**
   * Objeto para quitar la suscripción
   */
  public unsubscribe: Subject<void> = new Subject<void>();

  /**
   * Constructor del componente.
   * @param store Almacén de estados de la aplicación.
   */
  constructor(private store: Store<CounterModuleState>){
    this.store.select(x => x.counter)
    .subscribe( counter => this.counter = counter);
  }

  /**
  * Método que se ejecuta al destruir el componente.
  */
  ngOnDestroy(): void {
    this.unsubscribe.next();
    this.unsubscribe.complete();
  }

  /**
   * Incrementa el contador
   */
  increaseCounter(){
    this.store.dispatch(new IncreaseCounterAction);
    this.store.dispatch(new GetPokemonAction(this.counter));

  }

  /**
   * Decrementa el contador
   */
  decreaseCounter(){
    this.store.dispatch(new DecreaseCounterAction);
    this.store.dispatch(new GetPokemonAction(this.counter));
  }
}

<div class="relative row">
  <div class="h-40 grey darken-3"></div>

  <!-- Counter -->
  <div class="absolute flex w-screen h-64 md:h-96 md:justify-around justify-between items-end z-20">
    <button (click)="decreaseCounter()" class="btn-floating btn-large waves-effect grey lighten-5 left-align opacity-50 ml-16">
      <i class="material-icons right grey-text text-darken-1 opacity-1">navigate_before</i></button>
    <button (click)="increaseCounter()" class="btn-floating btn-large waves-effect grey lighten-5 right-align opacity-50 mr-16">
      <i class="material-icons right grey-text text-darken-1">navigate_next</i></button>
  </div>
  <!-- Pokedex component -->
  <div class="">
    <app-pokedex [pokemonId]="counter"></app-pokedex>
  </div>
</div>
