import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'pokedex-app';

  /**
   * Variable contador
   */
  contador: number;

  /**
   * Constructor
   */
  constructor(){
    this.contador = 10;
  }

  /**
   * Decrementa el contador
   */
  incrementar(){
    this.contador++;
  }

  /**
   * Incrementa el contador
   */
  decrementar(){
    this.contador--;
  }
}
