import { Component } from '@angular/core';
import { Store } from '@ngrx/store';
import { ModuleState } from './store/counter/app-state/module-state.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
/**
 * Clase que incrementa y decrementa el contador padre
 */
export class AppComponent {
  title = 'pokedex-app';

  /**
   * Variable contador
   */
  counter: number;

  /**
   * Constructor
   */
  constructor(private store: Store<ModuleState>){
    //this.counter = 10;
  }

  /**
   * Decrementa el contador
   */
  increaseCounter(){
    //this.counter++;
  }

  /**
   * Incrementa el contador
   */
  decreaseCounter(){
    //this.counter--;
  }
}
<div class="row center-align">
  <div class="">
    <h1>Contador</h1>
    <h2>{{counter}}</h2>
  </div>
</div>

<div class="row center-align">
  <div class="">
    <button (click)="increaseCounter()" class="waves-effect waves-light btn green mr-2"><i class="material-icons right">add</i>Incrementar</button>
    <button (click)="decreaseCounter()" class="waves-effect waves-light btn red ml-2"><i class="material-icons right">remove</i>Decrementar</button>
  </div>
</div>

<hr>
<div class="row center-align">
  <div class="">
    <app-child [counter] = "counter" (changeCounter)="counter = $event"></app-child>
  </div>
</div>
