import { EntityState } from "@ngrx/entity";
import { Pokemon } from "src/app/core/models/pokemon/pokemon.model";

/**
 * Estado del componente del pokemón.
 */
export interface PokemonState extends EntityState<Pokemon>{
    /**
     * Indica si se esta cargando el componente.
     */
    isLoading: boolean;
}

/**
 * Estado inicial del pokemón.
 */
export const initialStatePokemon: PokemonState = {
    isLoading: false,
    id: null,
    height: null,
    name: null,
    base_experience: null,
    sprites: {
        other:{
            dream_world:{
                front_default: null
            }
        }
    },
    stats: [{
        base_stat: null,
        effort: null,
        stat: {
            name: null
        }
    }],
    weight: null,
};
