import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-child',
  templateUrl: './child.component.html',
  styles: [
  ]
})
export class ChildComponent {

  /**
   * Contador que llega del padre
   */
  @Input() counter: number = 0; //Nuevas verciones de angular, se debe inicializar la variable
  /**
   * Emite un numero al padre por medio del @Output
   */
  @Output() changeCounter = new EventEmitter<number>();

  constructor(){

  }

  /**
   * Multiplica el contador en 2
   */
  multiplicar(){
    this.counter *= 2;
    this.changeCounter.emit(this.counter);

  }
  /**
   * Divide el contador en 2
   */
  dividir(){
    this.counter /= 2;
    this.changeCounter.emit(this.counter);

  }
}
<h3>Contador</h3>
<h4>{{counter}}</h4>

<div class="row">
    <div>
        <button (click)="multiplicar()" class="waves-effect waves-light btn grey darken-3 mr-2">Multiplicar</button>
        <button (click)="dividir()" class="waves-effect waves-light btn grey darken-3 ml-2">Dividir</button>
    </div>
</div>
